USE GOLD_DB;
GO

CREATE OR ALTER PROCEDURE CreatesQLServerlessView_gold (
    @ViewName NVARCHAR(100),
    @DataLakeUrl NVARCHAR(MAX) = N'https://datalakelabo4.dfs.core.windows.net/gold/SalesLT/'
)
WITH EXECUTE AS CALLER
AS
BEGIN
    DECLARE @statement NVARCHAR(MAX);
    SET @statement = N'CREATE OR ALTER VIEW ' + @ViewName + N' AS
                       SELECT * FROM OPENROWSET (BULK @DataLakeUrl + @ViewName + N'''', FORMAT = ''DELTA'') as [result]';

    EXEC sp_executesql @statement;
END;
GO
